    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): FruitViewHolder {
        return FruitViewHolder(
            LayoutInflater.from(parent.context)
                .inflate(R.layout.item_recyclerview_picture, parent, false)
        )
    }

    override fun getItemCount(): Int = listpicture.size

    override fun onBindViewHolder(holder: FruitViewHolder, position: Int) {
        holder.tvName.text = listpicture[position]
        holder.glide(listurl[position], holder.tvPosition)
    }


    class FruitViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
        var tvName = itemView.findViewById<TextView>(R.id.tv_name_item)
        var tvPosition = itemView.findViewById<ImageView>(R.id.imageview)

        fun glide(url: String, image: ImageView) {
            Glide.with(itemView.context)
                .load(url)
                .into(image)
        }

    }


}